import Imap from 'imap';
import * as nodemailer from 'nodemailer';
import { simpleParser } from 'mailparser';

export interface EmailConfig {
  imapServer: string;
  smtpServer: string;
  smtpPort: number;
  username: string;
  password: string;
  filterEmails: string[];
}

export interface ParsedEmail {
  from: string;
  subject: string;
  body: string;
  messageId: string;
}

export class EmailService {
  private config: EmailConfig;
  private imapConnection: any;
  private smtpTransporter: any;

  constructor() {
    this.config = {
      imapServer: process.env.IMAP_SERVER || 'imap.gmail.com',
      smtpServer: process.env.MAIL_SERVER || 'smtp.gmail.com',
      smtpPort: parseInt(process.env.MAIL_PORT || '587'),
      username: process.env.SMTP_USER || process.env.EMAIL_USER || '',
      password: process.env.SMTP_PASSWORD || process.env.EMAIL_PASSWORD || '',
      filterEmails: (process.env.FILTER_EMAILS || process.env.FILTER_EMAIL || '').split(',').map(email => email.trim()).filter(email => email),
    };

    this.initializeSmtp();
  }

  private initializeSmtp() {
    this.smtpTransporter = nodemailer.createTransport({
      host: this.config.smtpServer,
      port: this.config.smtpPort,
      secure: false,
      auth: {
        user: this.config.username,
        pass: this.config.password,
      },
    });
  }

  async connectImap(): Promise<void> {
    return new Promise((resolve, reject) => {
      this.imapConnection = new Imap({
        user: this.config.username,
        password: this.config.password,
        host: this.config.imapServer,
        port: 993,
        tls: true,
        tlsOptions: { rejectUnauthorized: false },
      });

      this.imapConnection.once('ready', () => {
        console.log('IMAP connection ready');
        resolve();
      });

      this.imapConnection.once('error', (err: Error) => {
        console.error('IMAP connection error:', err);
        reject(err);
      });

      this.imapConnection.connect();
    });
  }

  async checkForNewEmails(): Promise<ParsedEmail[]> {
    // If no credentials are configured, return empty array instead of failing
    if (!this.config.username || !this.config.password) {
      console.log('Email credentials not configured, returning empty emails array');
      return [];
    }

    if (!this.imapConnection) {
      try {
        await this.connectImap();
      } catch (error) {
        console.log('IMAP connection failed, returning empty emails array');
        return [];
      }
    }

    return new Promise((resolve, reject) => {
      this.imapConnection.openBox('INBOX', false, (err: Error) => {
        if (err) {
          console.log('IMAP openBox failed, returning empty emails array');
          resolve([]);
          return;
        }

        // Search for unread emails from filter emails
        let searchCriteria: any = ['UNSEEN'];
        if (this.config.filterEmails.length > 0) {
          // Create OR criteria for multiple email addresses
          const fromCriteria = this.config.filterEmails.map(email => ['FROM', email]);
          if (fromCriteria.length === 1) {
            searchCriteria = [searchCriteria, fromCriteria[0]];
          } else {
            searchCriteria = [searchCriteria, ['OR', ...fromCriteria]];
          }
        }

        this.imapConnection.search(searchCriteria, (err: Error, results: number[]) => {
          if (err) {
            reject(err);
            return;
          }

          if (!results || results.length === 0) {
            resolve([]);
            return;
          }

          const emails: ParsedEmail[] = [];
          let processed = 0;

          const fetchOptions = {
            bodies: '',
            markSeen: true,
          };

          const fetch = this.imapConnection.fetch(results, fetchOptions);

          fetch.on('message', (msg: any) => {
            let emailData = '';

            msg.on('body', (stream: any) => {
              stream.on('data', (chunk: Buffer) => {
                emailData += chunk.toString('utf8');
              });
            });

            msg.on('end', async () => {
              try {
                const parsed = await simpleParser(emailData);

                emails.push({
                  from: parsed.from?.text || '',
                  subject: parsed.subject || '',
                  body: parsed.text || '',
                  messageId: parsed.messageId || '',
                });

                processed++;
                if (processed === results.length) {
                  resolve(emails);
                }
              } catch (parseErr) {
                console.error('Error parsing email:', parseErr);
                processed++;
                if (processed === results.length) {
                  resolve(emails);
                }
              }
            });
          });

          fetch.once('error', (fetchErr: Error) => {
            reject(fetchErr);
          });

          fetch.once('end', () => {
            if (processed === 0) {
              resolve([]);
            }
          });
        });
      });
    });
  }

  async sendEmailWithAttachment(to: string, subject: string, body: string, attachmentPath?: string): Promise<void> {
    const mailOptions: any = {
      from: this.config.username,
      to,
      subject,
      text: body,
    };

    if (attachmentPath) {
      const filename = attachmentPath.split('/').pop() || 'analysis_result.png';
      mailOptions.attachments = [{
        filename: filename,
        path: attachmentPath,
        contentType: 'image/png'
      }];
    }

    try {
      await this.smtpTransporter.sendMail(mailOptions);
      console.log(`Email sent successfully to ${to}`);
    } catch (error) {
      console.error('Error sending email:', error);
      throw error;
    }
  }

  async testConnection(): Promise<boolean> {
    // If no credentials are configured, return false but don't throw errors
    if (!this.config.username || !this.config.password) {
      console.log('Email credentials not configured');
      return false;
    }

    try {
      await this.smtpTransporter.verify();
      await this.connectImap();
      return true;
    } catch (error) {
      console.log('Email connection test failed:', error);
      return false;
    }
  }

  disconnect(): void {
    if (this.imapConnection) {
      this.imapConnection.end();
    }
  }
}

server/services/llmService.ts.content:
import axios from "axios";
import { mcpResourceService } from "./mcpResourceService.js";

export interface LLMResponse {
  script: string;
  lasFile: string;
  tool: string;
  confidence: number;
  reasoning: string;
}

export class LLMService {
  private endpoint: string;
  private model: string;
  private provider: string;
  private apiKey?: string;

  constructor() {
    // Set default Ollama configuration from environment or fallback
    this.endpoint = (
      process.env.OLLAMA_ENDPOINT || "https://88c46355da8c.ngrok-free.app"
    ).replace(/\/$/, "");
    this.model = "llama3.2:1b";
    this.provider = "ollama";
    this.apiKey = undefined;
  }

  // Accept configuration from frontend localStorage
  setConfig(config: {
    provider: string;
    model: string;
    endpoint?: string;
    apiKey?: string;
  }) {
    this.provider = config.provider;
    this.model = config.model;
    this.endpoint = config.endpoint ? config.endpoint.replace(/\/$/, "") : "";
    this.apiKey = config.apiKey;
  }

  // Use default configuration if not provided
  private useDefaults() {
    if (!this.endpoint || !this.provider || !this.model) {
      this.endpoint = (
        process.env.OLLAMA_ENDPOINT || "https://88c46355da8c.ngrok-free.app"
      ).replace(/\/$/, "");
      this.model = "llama3.2:1b";
      this.provider = "ollama";
      this.apiKey = undefined;
    }
  }

  async analyzeEmailContent(
    emailBody: string,
    config?: {
      provider: string;
      model: string;
      endpoint?: string;
      apiKey?: string;
    },
  ): Promise<LLMResponse> {
    // Use provided config or defaults
    if (config) {
      this.setConfig(config);
    } else {
      this.useDefaults();
    }

    // Get MCP resources for the LLM
    await mcpResourceService.discoverResources();
    const resourceInfo = mcpResourceService.getResourcesForLLM();

    const prompt = `Analyze this email request for LAS (Log ASCII Standard) file processing:

Email content: "${emailBody}"

${resourceInfo}

IMPORTANT: You can ONLY use these EXACT available resources:

SCRIPTS (choose exactly one):
- "depth_visualization.py" → for depth plots, visualizations, general plotting
- "gamma_ray_analyzer.py" → for gamma ray analysis, statistical analysis
- "resistivity_analyzer.py" → for resistivity analysis and formation evaluation
- "porosity_calculator.py" → for porosity calculations from neutron/density logs
- "lithology_classifier.py" → for rock type classification and lithology analysis

LAS FILES (choose exactly one):
- "sample_well_01.las" → for basic well data, depth analysis
- "production_well_02.las" → for production data, gamma ray analysis
- "offshore_well_03.las" → for offshore drilling data, comprehensive logs
- "exploration_well_04.las" → for exploration data, deep resistivity analysis
- "development_well_05.las" → for development data, advanced logging suite

TOOLS (must match script EXACTLY):
- "depth_plotter" → ONLY use with depth_visualization.py
- "gamma_analyzer" → ONLY use with gamma_ray_analyzer.py
- "resistivity_analyzer" → ONLY use with resistivity_analyzer.py
- "porosity_calculator" → ONLY use with porosity_calculator.py
- "lithology_classifier" → ONLY use with lithology_classifier.py

You must respond with a JSON object containing:
- script: Choose exactly one from: "depth_visualization.py", "gamma_ray_analyzer.py", "resistivity_analyzer.py", "porosity_calculator.py", "lithology_classifier.py"
- lasFile: Choose exactly one from: "sample_well_01.las", "production_well_02.las", "offshore_well_03.las", "exploration_well_04.las", "development_well_05.las"
- tool: Choose exactly one from: "depth_plotter", "gamma_analyzer", "resistivity_analyzer", "porosity_calculator", "lithology_classifier"
- confidence: A number between 0-1 indicating confidence in the analysis
- reasoning: A brief explanation of why these choices were made

STRICT mapping rules (FOLLOW EXACTLY):
- ANY mention of "depth_visualization.py" → depth_visualization.py + depth_plotter + sample_well_01.las
- ANY mention of "gamma_ray_analyzer.py" → gamma_ray_analyzer.py + gamma_analyzer + production_well_02.las
- ANY mention of "resistivity_analyzer.py" → resistivity_analyzer.py + resistivity_analyzer + exploration_well_04.las
- ANY mention of "porosity_calculator.py" → porosity_calculator.py + porosity_calculator + offshore_well_03.las
- ANY mention of "lithology_classifier.py" → lithology_classifier.py + lithology_classifier + development_well_05.las

Keywords mapping (when no script specified) - USE FIRST MATCH FOUND:
Priority order (most specific first):
1. "gamma", "gamma ray", "radioactivity" → gamma_ray_analyzer.py + gamma_analyzer + production_well_02.las
2. "resistivity", "resistance", "formation evaluation" → resistivity_analyzer.py + resistivity_analyzer + exploration_well_04.las
3. "porosity", "neutron", "density", "pore space" → porosity_calculator.py + porosity_calculator + offshore_well_03.las
4. "lithology", "rock type", "classification", "formation" → lithology_classifier.py + lithology_classifier + development_well_05.las
5. "plot", "depth", "visualization", "chart" → depth_visualization.py + depth_plotter + sample_well_01.las

CRITICAL: If ANY of the first 4 keywords are found, DO NOT use depth_visualization.py!
Examples:
- "create gamma ray plot" = GAMMA RAY → gamma_ray_analyzer.py (NOT depth_visualization.py)
- "resistivity visualization" = RESISTIVITY → resistivity_analyzer.py (NOT depth_visualization.py)
- "porosity chart" = POROSITY → porosity_calculator.py (NOT depth_visualization.py)

CRITICAL RULES - FOLLOW EXACTLY:
1. If user specifies a script name OR LAS file, USE EXACTLY what they specify!
2. If user mentions "lithology" anywhere, ALWAYS use lithology_classifier.py + lithology_classifier + development_well_05.las
3. If user mentions "gamma" anywhere, ALWAYS use gamma_ray_analyzer.py + gamma_analyzer + production_well_02.las
4. If user mentions "resistivity" anywhere, ALWAYS use resistivity_analyzer.py + resistivity_analyzer + exploration_well_04.las
5. If user mentions "porosity" anywhere, ALWAYS use porosity_calculator.py + porosity_calculator + offshore_well_03.las

FORBIDDEN: Never mix different analysis types. If user asks for lithology, DO NOT give gamma ray analysis!

Example responses:
{
  "script": "gamma_ray_analyzer.py",
  "lasFile": "production_well_02.las",
  "tool": "gamma_analyzer",
  "confidence": 0.95,
  "reasoning": "Request mentions gamma ray, using gamma ray analysis with production well data"
}

{
  "script": "lithology_classifier.py",
  "lasFile": "development_well_05.las",
  "tool": "lithology_classifier",
  "confidence": 0.95,
  "reasoning": "Request mentions lithology classification, using lithology classifier with development well data"
}

{
  "script": "resistivity_analyzer.py",
  "lasFile": "exploration_well_04.las",
  "tool": "resistivity_analyzer",
  "confidence": 0.90,
  "reasoning": "Request asks for resistivity analysis, using exploration data with deep resistivity logs"
}

{
  "script": "depth_visualization.py",
  "lasFile": "sample_well_01.las",
  "tool": "depth_plotter",
  "confidence": 0.85,
  "reasoning": "Request asks for basic plotting/visualization only, using sample well data"
}

Respond only with valid JSON:`;

    try {
      const response = await axios.post(
        `${this.endpoint}/api/generate`,
        {
          model: this.model,
          prompt,
          stream: false,
          format: "json",
        },
        {
          headers: {
            "Content-Type": "application/json",
          },
          timeout: 45000,
        },
      );

      const responseText = response.data.response;

      try {
        const parsed = JSON.parse(responseText);

        // Validate required fields
        if (!parsed.script || !parsed.lasFile || !parsed.tool) {
          throw new Error("Missing required fields in LLM response");
        }

        return {
          script: parsed.script,
          lasFile: parsed.lasFile,
          tool: parsed.tool,
          confidence: parsed.confidence || 0.5,
          reasoning: parsed.reasoning || "No reasoning provided",
        };
      } catch (parseError) {
        console.error("Failed to parse LLM response as JSON:", responseText);
        throw new Error(
          `LLM returned invalid response: ${responseText.substring(0, 100)}...`,
        );
      }
    } catch (error) {
      console.error("LLM service error:", error);
      throw new Error("LLM service unavailable: " + (error instanceof Error ? error.message : 'Unknown error'));
    }
  }

  async checkClarification(
    query: string,
    config?: {
      provider: string;
      model: string;
      endpoint?: string;
      apiKey?: string;
    },
  ): Promise<{
    needsClarification: boolean;
    confidence: number;
    suggestions: string[];
    message: string;
  }> {
    // Use provided config or defaults
    if (config) {
      this.setConfig(config);
    } else {
      this.useDefaults();
    }

    const prompt = `Analyze this LAS analysis query and determine if it needs clarification:

Query: "${query}"

Available options:
- "depth visualization" using depth_visualization.py with sample_well_01.las
- "gamma ray analysis" using gamma_ray_analyzer.py with production_well_02.las
- "resistivity analysis" using resistivity_analyzer.py with exploration_well_04.las
- "porosity calculation" using porosity_calculator.py with offshore_well_03.las
- "lithology classification" using lithology_classifier.py with development_well_05.las

Respond with JSON containing:
- needsClarification: true if query is unclear or ambiguous
- confidence: 0.0-1.0 confidence in understanding the request
- suggestions: array of clarification options if needed
- message: helpful response message

Example responses:
{
  "needsClarification": false,
  "confidence": 0.9,
  "suggestions": [],
  "message": "I understand you want gamma ray analysis. Let me process that."
}

{
  "needsClarification": true,
  "confidence": 0.3,
  "suggestions": [
    "Depth visualization with sample_well_01.las", 
    "Gamma ray analysis with production_well_02.las",
    "Resistivity analysis with exploration_well_04.las",
    "Porosity calculation with offshore_well_03.las",
    "Lithology classification with development_well_05.las"
  ],
  "message": "I'm not sure what type of analysis you need. Could you clarify?"
}

Respond only with valid JSON:`;

    try {
      const response = await axios.post(
        `${this.endpoint}/api/generate`,
        {
          model: this.model,
          prompt,
          stream: false,
          format: "json",
        },
        {
          headers: {
            "Content-Type": "application/json",
          },
          timeout: 30000,
        },
      );

      const responseText = response.data.response;

      try {
        const parsed = JSON.parse(responseText);
        return {
          needsClarification: parsed.needsClarification || false,
          confidence: parsed.confidence || 0.5,
          suggestions: parsed.suggestions || [],
          message:
            parsed.message || "Let me help you with your analysis request.",
        };
      } catch (parseError) {
        console.error(
          "Failed to parse clarification response as JSON:",
          responseText,
        );
        throw new Error("Invalid JSON response from LLM service");
      }
    } catch (error) {
      console.error("LLM clarification error:", error);
      throw new Error(
        `LLM service unavailable: ${error instanceof Error ? error.message : "Unknown error"}`,
      );
    }
  }

  async testConnection(config?: {
    provider: string;
    model: string;
    endpoint?: string;
    apiKey?: string;
  }): Promise<{ success: boolean; responseTime?: number; error?: string }> {
    // Use provided config or defaults
    if (config) {
      this.setConfig(config);
    } else {
      this.useDefaults();
    }

    if (!this.endpoint || !this.provider || !this.model) {
      return {
        success: false,
        responseTime: 0,
        error:
          "LLM not configured. Please configure provider, model, and endpoint in settings.",
      };
    }

    const startTime = Date.now();

    try {
      const response = await axios.post(
        `${this.endpoint}/api/generate`,
        {
          model: this.model,
          prompt: "Test connection. Respond with: OK",
          stream: false,
        },
        {
          headers: {
            "Content-Type": "application/json",
          },
          timeout: 15000,
        },
      );

      const responseTime = Date.now() - startTime;

      return {
        success: true,
        responseTime: responseTime / 1000, // Convert to seconds
      };
    } catch (error) {
      const responseTime = Date.now() - startTime;
      return {
        success: false,
        responseTime: responseTime / 1000,
        error:
          error instanceof Error ? error.message : "LLM service unavailable",
      };
    }
  }
}

server/services/mcpService.ts.content:
import * as fs from 'fs';
import * as path from 'path';
import { spawn } from 'child_process';

export interface MCPResource {
  name: string;
  type: 'las_file' | 'script' | 'tool';
  path: string;
  size?: number;
  lastModified?: Date;
}

export interface MCPProcessingResult {
  success: boolean;
  outputPath?: string;
  processingTime: number;
  error?: string;
}

export class MCPService {
  private resourcesPath: string;
  private outputPath: string;
  private availableTools: string[];

  constructor() {
    this.resourcesPath = process.env.MCP_RESOURCES_PATH || './mcp_resources';
    this.outputPath = process.env.MCP_OUTPUT_PATH || './output';
    this.availableTools = [
      'depth_plotter',
      'gamma_analyzer', 
      'resistivity_analyzer',
      'porosity_calculator',
      'lithology_classifier'
    ];

    this.initializeDirectories();
  }

  private initializeDirectories(): void {
    // Create directories if they don't exist
    [this.resourcesPath, this.outputPath].forEach(dir => {
      if (!fs.existsSync(dir)) {
        fs.mkdirSync(dir, { recursive: true });
      }
    });

    // Create subdirectories
    const subdirs = ['las_files', 'scripts', 'tools'];
    subdirs.forEach(subdir => {
      const fullPath = path.join(this.resourcesPath, subdir);
      if (!fs.existsSync(fullPath)) {
        fs.mkdirSync(fullPath, { recursive: true });
      }
    });
  }

  async getResources(): Promise<MCPResource[]> {
    const resources: MCPResource[] = [];

    try {
      // Get LAS files
      const lasFilesPath = path.join(this.resourcesPath, 'las_files');
      if (fs.existsSync(lasFilesPath)) {
        const lasFiles = fs.readdirSync(lasFilesPath);
        for (const file of lasFiles) {
          if (file.endsWith('.las')) {
            const filePath = path.join(lasFilesPath, file);
            const stats = fs.statSync(filePath);
            resources.push({
              name: file,
              type: 'las_file',
              path: filePath,
              size: stats.size,
              lastModified: stats.mtime,
            });
          }
        }
      }

      // Get scripts
      const scriptsPath = path.join(this.resourcesPath, 'scripts');
      if (fs.existsSync(scriptsPath)) {
        const scripts = fs.readdirSync(scriptsPath);
        for (const file of scripts) {
          if (file.endsWith('.py')) {
            const filePath = path.join(scriptsPath, file);
            const stats = fs.statSync(filePath);
            resources.push({
              name: file,
              type: 'script',
              path: filePath,
              size: stats.size,
              lastModified: stats.mtime,
            });
          }
        }
      }

      // Add available tools
      this.availableTools.forEach(tool => {
        resources.push({
          name: tool,
          type: 'tool',
          path: `/mcp/tools/${tool}`,
        });
      });

    } catch (error) {
      console.error('Error getting MCP resources:', error);
    }

    return resources;
  }

  async processLASFile(
    lasFileName: string,
    scriptName: string,
    toolName: string
  ): Promise<MCPProcessingResult> {
    const startTime = Date.now();

    try {
      // Check if LAS file exists
      const lasFilePath = path.join(this.resourcesPath, 'las_files', lasFileName);
      if (!fs.existsSync(lasFilePath)) {
        return {
          success: false,
          processingTime: Date.now() - startTime,
          error: `LAS file not found: ${lasFileName}`,
        };
      }

      // Check if script exists
      const scriptPath = path.join(this.resourcesPath, 'scripts', scriptName);
      if (!fs.existsSync(scriptPath)) {
        return {
          success: false,
          processingTime: Date.now() - startTime,
          error: `Script not found: ${scriptName}`,
        };
      }

      // Check if tool is available
      if (!this.availableTools.includes(toolName)) {
        return {
          success: false,
          processingTime: Date.now() - startTime,
          error: `Tool not available: ${toolName}`,
        };
      }

      // Create timestamped output directory with proper date formatting
      const now = new Date();
      const timestamp = now.toISOString().replace(/[:.]/g, '').replace(/T/, '_').slice(0, 15); // YYYY-MM-DD_HHMMSS
      const friendlyTimestamp = `${now.getFullYear()}-${(now.getMonth() + 1).toString().padStart(2, '0')}-${now.getDate().toString().padStart(2, '0')}_${now.getHours().toString().padStart(2, '0')}-${now.getMinutes().toString().padStart(2, '0')}-${now.getSeconds().toString().padStart(2, '0')}`;
      const outputDir = path.join(this.outputPath, friendlyTimestamp);
      fs.mkdirSync(outputDir, { recursive: true });

      // Generate output file path
      const outputFileName = `${path.basename(lasFileName, '.las')}_${toolName}_output.png`;
      const outputFilePath = path.join(outputDir, outputFileName);

      // Actually execute the Python script
      await this.executeScript(scriptPath, lasFilePath, outputFilePath);

      const processingTime = Date.now() - startTime;

      return {
        success: true,
        outputPath: outputFilePath,
        processingTime,
      };

    } catch (error) {
      return {
        success: false,
        processingTime: Date.now() - startTime,
        error: error instanceof Error ? error.message : 'Unknown processing error',
      };
    }
  }

  private async simulateProcessing(scriptName: string, toolName: string): Promise<void> {
    // Simulate processing time based on tool complexity
    const processingTimes: Record<string, number> = {
      'depth_plotter': 1000,
      'gamma_analyzer': 1500,
      'resistivity_tool': 2000,
      'porosity_calculator': 1200,
      'lithology_detector': 2500,
      'well_correlation': 3000,
      'log_quality_checker': 800,
      'formation_evaluator': 3500,
    };

    const delay = processingTimes[toolName] || 1000;
    await new Promise(resolve => setTimeout(resolve, delay));
  }

  private async createPlaceholderPNG(outputPath: string): Promise<void> {
    // Create a minimal PNG file (1x1 pixel transparent)
    const pngData = Buffer.from([
      0x89, 0x50, 0x4E, 0x47, 0x0D, 0x0A, 0x1A, 0x0A, // PNG signature
      0x00, 0x00, 0x00, 0x0D, // IHDR chunk length
      0x49, 0x48, 0x44, 0x52, // IHDR
      0x00, 0x00, 0x00, 0x01, // width: 1
      0x00, 0x00, 0x00, 0x01, // height: 1
      0x08, 0x06, 0x00, 0x00, 0x00, // bit depth, color type, etc.
      0x1F, 0x15, 0xC4, 0x89, // CRC
      0x00, 0x00, 0x00, 0x0A, // IDAT chunk length
      0x49, 0x44, 0x41, 0x54, // IDAT
      0x78, 0x9C, 0x62, 0x00, 0x00, 0x00, 0x02, 0x00, 0x01, // compressed data
      0xE2, 0x21, 0xBC, 0x33, // CRC
      0x00, 0x00, 0x00, 0x00, // IEND chunk length
      0x49, 0x45, 0x4E, 0x44, // IEND
      0xAE, 0x42, 0x60, 0x82  // CRC
    ]);

    fs.writeFileSync(outputPath, pngData);
  }

  async getToolStatus(): Promise<{ available: number; total: number; tools: string[] }> {
    return {
      available: this.availableTools.length,
      total: this.availableTools.length,
      tools: [...this.availableTools],
    };
  }

  private async executeScript(scriptPath: string, lasFilePath: string, outputPath: string): Promise<void> {
    return new Promise((resolve, reject) => {
      console.log(`Executing script: ${scriptPath} with LAS file: ${lasFilePath}`);

      const pythonProcess = spawn('uv', ['run', 'python', scriptPath, lasFilePath, outputPath], {
        stdio: ['pipe', 'pipe', 'pipe']
      });

      let stdout = '';
      let stderr = '';

      pythonProcess.stdout.on('data', (data) => {
        stdout += data.toString();
        console.log(`Python stdout: ${data.toString().trim()}`);
      });

      pythonProcess.stderr.on('data', (data) => {
        stderr += data.toString();
        console.error(`Python stderr: ${data.toString().trim()}`);
      });

      pythonProcess.on('close', (code) => {
        if (code === 0) {
          console.log(`Script executed successfully: ${scriptPath}`);
          resolve();
        } else {
          console.error(`Script execution failed with code ${code}`);
          console.error(`Stderr: ${stderr}`);
          reject(new Error(`Script execution failed with code ${code}: ${stderr}`));
        }
      });

      pythonProcess.on('error', (error) => {
        console.error(`Failed to start Python process: ${error.message}`);
        reject(error);
      });
    });
  }

  async testConnection(): Promise<boolean> {
    try {
      // Test if we can access resources
      await this.getResources();
      return true;
    } catch (error) {
      console.error('MCP service test failed:', error);
      return false;
    }
  }
}

client/src/components/ActivityLog.tsx.content:
import { CheckCircle, Mail, AlertTriangle, ExternalLink } from "lucide-react";

interface EmailLog {
  id: string;
  sender: string;
  subject: string;
  body: string;
  status: string;
  createdAt: string;
  processingTime: number;
  outputFile: string;
  errorMessage: string;
}

interface ActivityLogProps {
  emailLogs: EmailLog[];
}

const getStatusIcon = (status: string) => {
  switch (status) {
    case 'completed':
      return <CheckCircle className="h-4 w-4 text-success" />;
    case 'processing':
      return <Mail className="h-4 w-4 text-primary" />;
    case 'error':
      return <AlertTriangle className="h-4 w-4 text-destructive" />;
    default:
      return <Mail className="h-4 w-4 text-muted-foreground" />;
  }
};

const getStatusColor = (status: string) => {
  switch (status) {
    case 'completed':
      return 'bg-success/10';
    case 'processing':
      return 'bg-primary/10';
    case 'error':
      return 'bg-destructive/10';
    default:
      return 'bg-muted/10';
  }
};

const formatTimeAgo = (dateString: string) => {
  const date = new Date(dateString);
  const now = new Date();
  const diffInMinutes = Math.floor((now.getTime() - date.getTime()) / (1000 * 60));

  if (diffInMinutes < 1) return 'Just now';
  if (diffInMinutes < 60) return `${diffInMinutes} minute${diffInMinutes === 1 ? '' : 's'} ago`;

  const diffInHours = Math.floor(diffInMinutes / 60);
  if (diffInHours < 24) return `${diffInHours} hour${diffInHours === 1 ? '' : 's'} ago`;

  return date.toLocaleDateString();
};

export default function ActivityLog({ emailLogs }: ActivityLogProps) {
  return (
    <div className="bg-card rounded-lg border border-border">
      <div className="p-6 border-b border-border">
        <div className="flex items-center justify-between">
          <h3 className="text-lg font-semibold text-foreground">Recent Activity</h3>
          <button className="text-muted-foreground hover:text-foreground" data-testid="button-view-all-logs">
            <ExternalLink className="h-4 w-4" />
          </button>
        </div>
      </div>
      <div className="p-6">
        <div className="space-y-4">
          {emailLogs.length === 0 ? (
            <div className="text-center py-8">
              <Mail className="h-8 w-8 text-muted-foreground mx-auto mb-3" />
              <p className="text-muted-foreground">No recent activity</p>
            </div>
          ) : (
            emailLogs.slice(0, 5).map((log) => (
              <div key={log.id} className="log-entry flex items-start space-x-3 p-3 rounded-md" data-testid={`activity-log-${log.id}`}>
                <div className={`w-8 h-8 ${getStatusColor(log.status)} rounded-full flex items-center justify-center flex-shrink-0 mt-0.5`}>
                  {getStatusIcon(log.status)}
                </div>
                <div className="flex-1 min-w-0">
                  <p className="text-sm font-medium text-foreground">
                    {log.status === 'completed' && 'Email processed successfully from '}
                    {log.status === 'processing' && 'Processing email from '}
                    {log.status === 'error' && 'Processing failed for '}
                    <span className="text-primary">{log.sender}</span>
                  </p>
                  {log.outputFile && (
                    <p className="text-xs text-muted-foreground mt-1">
                      Generated: {log.outputFile.split('/').pop()}
                    </p>
                  )}
                  {log.errorMessage && (
                    <p className="text-xs text-destructive mt-1">
                      Error: {log.errorMessage}
                    </p>
                  )}
                  <p className="text-xs text-muted-foreground" data-testid={`text-activity-timestamp-${log.id}`}>
                    {formatTimeAgo(log.createdAt)}
                  </p>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
}

client/src/components/LLMPanel.tsx.content:
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useMutation } from "@tanstack/react-query";
import { TestTubeDiagonal, Save, RefreshCw } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { useState, useEffect } from "react";
import { llmConfigStorage, type LLMConfig } from "@/lib/llmConfigStorage";

interface LLMPanelProps {
  status?: {
    id: string;
    component: string;
    status: string;
    lastCheck: string;
    metadata: any;
  };
}

const LLM_PROVIDERS = [
  { value: 'ollama', label: 'Ollama' },
  { value: 'openai', label: 'OpenAI' },
  { value: 'gemini', label: 'Google Gemini' },
  { value: 'claude', label: 'Anthropic Claude' },
  { value: 'mistral', label: 'Mistral AI' },
  { value: 'cohere', label: 'Cohere' },
  { value: 'huggingface', label: 'Hugging Face' },
  { value: 'local', label: 'Local Model' }
];

const OLLAMA_MODELS = [
  'llama3.2:1b',
  'llama3.2:3b', 
  'llama3.1:8b',
  'llama3.1:70b',
  'mistral:7b',
  'codellama:7b',
  'qwen2.5:7b',
  'gemma2:9b',
  'phi3:mini',
  'llava:7b'
];

const OPENAI_MODELS = [
  'gpt-4o',
  'gpt-4o-mini',
  'gpt-4-turbo',
  'gpt-3.5-turbo',
  'text-davinci-003'
];

const GEMINI_MODELS = [
  'gemini-1.5-pro',
  'gemini-1.5-flash',
  'gemini-pro',
  'gemini-pro-vision'
];

const CLAUDE_MODELS = [
  'claude-3-5-sonnet-20241022',
  'claude-3-opus-20240229',
  'claude-3-sonnet-20240229',
  'claude-3-haiku-20240307'
];

const MISTRAL_MODELS = [
  'mistral-large-latest',
  'mistral-medium-latest',
  'mistral-small-latest',
  'codestral-latest'
];

export default function LLMPanel({ status }: LLMPanelProps) {
  const { toast } = useToast();
  const [provider, setProvider] = useState('');
  const [selectedModel, setSelectedModel] = useState('');
  const [endpoint, setEndpoint] = useState('');
  const [apiKey, setApiKey] = useState('');
  const [isConfigured, setIsConfigured] = useState(false);

  const isConnected = status?.status === 'online';
  const responseTime = status?.metadata?.responseTime || '0';
  const successRate = 94; // This would come from historical data

  // Load configuration from localStorage when component mounts
  useEffect(() => {
    const savedConfig = llmConfigStorage.get();
    if (savedConfig) {
      setProvider(savedConfig.provider);
      setSelectedModel(savedConfig.model);
      setEndpoint(savedConfig.endpoint || '');
      setApiKey(savedConfig.apiKey || '');
      setIsConfigured(true);
    } else {
      // Load default configuration
      const defaultConfig = llmConfigStorage.getDefault();
      setProvider(defaultConfig.provider);
      setSelectedModel(defaultConfig.model);
      setEndpoint(defaultConfig.endpoint || '');
      setIsConfigured(false);
    }
  }, []);

  const testConnectionMutation = useMutation({
    mutationFn: async () => {
      // Send current configuration for testing
      const currentConfig = {
        provider,
        model: selectedModel,
        endpoint: requiresEndpoint() ? endpoint : undefined,
        apiKey: requiresApiKey() ? apiKey : undefined
      };

      const response = await fetch("/api/test-connections", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ llmConfig: currentConfig }),
      });
      if (!response.ok) throw new Error("Failed to test connections");
      return response.json();
    },
    onSuccess: (data) => {
      if (data.llm.success) {
        toast({
          title: "LLM Connection Test Successful",
          description: `Response time: ${data.llm.responseTime?.toFixed(2)}s`,
        });
      } else {
        toast({
          title: "LLM Connection Test Failed",
          description: data.llm.error || "Unknown error",
          variant: "destructive",
        });
      }
    },
    onError: () => {
      toast({
        title: "Test Failed",
        description: "Failed to test LLM connection.",
        variant: "destructive",
      });
    },
  });

  const saveConfigurationMutation = useMutation({
    mutationFn: async (config: LLMConfig) => {
      // Save to localStorage directly
      llmConfigStorage.set(config);

      // Also send to backend to update system status
      const response = await fetch('/api/llm-config', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(config)
      });
      if (!response.ok) throw new Error('Failed to update system status');
      return response.json();
    },
    onSuccess: () => {
      setIsConfigured(true);
      toast({
        title: 'Configuration Updated',
        description: 'LLM configuration has been saved to local storage.'
      });
    },
    onError: (error) => {
      toast({
        title: 'Update Failed',
        description: 'Failed to save LLM configuration: ' + error.message,
        variant: 'destructive'
      });
    }
  });

  const handleUpdateConfiguration = () => {
    if (!provider) {
      toast({
        title: "Validation Error",
        description: "Please select a provider",
        variant: "destructive"
      });
      return;
    }

    if (!selectedModel) {
      toast({
        title: "Validation Error", 
        description: "Please select a model",
        variant: "destructive"
      });
      return;
    }

    if (requiresEndpoint() && !endpoint) {
      toast({
        title: "Validation Error",
        description: "Please enter an endpoint URL",
        variant: "destructive"
      });
      return;
    }

    if (requiresApiKey() && !apiKey) {
      toast({
        title: "Validation Error",
        description: "Please enter an API key",
        variant: "destructive"
      });
      return;
    }

    const config = {
      provider,
      model: selectedModel,
      endpoint: requiresEndpoint() ? endpoint : undefined,
      apiKey: requiresApiKey() ? apiKey : undefined
    };
    saveConfigurationMutation.mutate(config);
  };

  const getModelsForProvider = () => {
    switch (provider) {
      case 'ollama':
        return OLLAMA_MODELS;
      case 'openai':
        return OPENAI_MODELS;
      case 'gemini':
        return GEMINI_MODELS;
      case 'claude':
        return CLAUDE_MODELS;
      case 'mistral':
        return MISTRAL_MODELS;
      default:
        return ['custom-model'];
    }
  };

  const requiresApiKey = () => {
    return provider && provider !== 'ollama' && provider !== 'local';
  };

  const requiresEndpoint = () => {
    return provider === 'ollama' || provider === 'local';
  };

  return (
    <div className="bg-card rounded-lg border border-border">
      <div className="p-6 border-b border-border">
        <div className="flex items-center justify-between">
          <h3 className="text-lg font-semibold text-foreground">LLM Integration</h3>
          <div className="flex items-center gap-2">
            <span className={`px-2 py-1 text-xs rounded-full ${isConnected ? 'bg-success/10 text-success' : 'bg-destructive/10 text-destructive'}`}>
              {isConnected ? 'Connected' : 'Disconnected'}
            </span>
          </div>
        </div>
      </div>

      <div className="p-6 space-y-6">
        {/* Setup Instructions */}
        {!isConfigured && (
          <div className="bg-muted/50 rounded-lg p-4 border border-border">
            <h4 className="text-sm font-medium text-foreground mb-2">Setup Instructions</h4>
            <div className="text-xs text-muted-foreground space-y-1">
              <p><strong>For Ollama:</strong> Install Ollama locally and run <code className="bg-muted px-1 rounded">ollama serve</code></p>
              <p><strong>For API Services:</strong> Get an API key from your provider (OpenAI, Anthropic, etc.)</p>
              <p><strong>Endpoint Examples:</strong> http://localhost:11434 (Ollama) or https://api.openai.com (OpenAI)</p>
              <p><strong>Storage:</strong> Configuration is saved locally in your browser</p>
            </div>
          </div>
        )}

        {/* LLM Provider Selection */}
        <div className="space-y-2">
          <Label htmlFor="provider" className="text-sm font-medium text-foreground">LLM Provider</Label>
          <Select value={provider} onValueChange={(value) => {
            setProvider(value);
            setSelectedModel(''); // Reset model when provider changes
          }} data-testid="select-llm-provider">
            <SelectTrigger>
              <SelectValue placeholder="Select LLM Provider" />
            </SelectTrigger>
            <SelectContent>
              {LLM_PROVIDERS.map((p) => (
                <SelectItem key={p.value} value={p.value}>
                  {p.label}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>

        {/* Model Selection */}
        {provider && (
          <div className="space-y-2">
            <Label htmlFor="model" className="text-sm font-medium text-foreground">Model</Label>
            <Select value={selectedModel} onValueChange={setSelectedModel} data-testid="select-llm-model">
              <SelectTrigger>
                <SelectValue placeholder="Select Model" />
              </SelectTrigger>
              <SelectContent>
                {getModelsForProvider().map((model) => (
                  <SelectItem key={model} value={model}>
                    {model}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>
        )}

        {/* Endpoint URL (for Ollama/Local) */}
        {requiresEndpoint() && (
          <div className="space-y-2">
            <Label htmlFor="endpoint" className="text-sm font-medium text-foreground">Endpoint URL</Label>
            <Input
              id="endpoint"
              type="url"
              placeholder="http://localhost:11434"
              value={endpoint}
              onChange={(e) => setEndpoint(e.target.value)}
              data-testid="input-llm-endpoint"
            />
            <p className="text-xs text-muted-foreground">
              Enter your {provider === 'ollama' ? 'Ollama' : 'local'} server endpoint URL
            </p>
          </div>
        )}

        {/* API Key (for cloud providers) */}
        {requiresApiKey() && (
          <div className="space-y-2">
            <Label htmlFor="apiKey" className="text-sm font-medium text-foreground">API Key</Label>
            <Input
              id="apiKey"
              type="password"
              placeholder="Enter your API key"
              value={apiKey}
              onChange={(e) => setApiKey(e.target.value)}
              data-testid="input-llm-api-key"
            />
            <p className="text-xs text-muted-foreground">
              Your API key for {LLM_PROVIDERS.find(p => p.value === provider)?.label}
            </p>
          </div>
        )}

        {/* Configuration Status */}
        {provider && (
          <div className="bg-muted rounded-lg p-4 space-y-3">
            <div className="flex items-center justify-between">
              <h4 className="font-medium text-foreground">Current Configuration</h4>
              <div className="flex items-center gap-2">
                <span className={`px-2 py-1 text-xs rounded-full ${
                  isConfigured ? 'bg-success/10 text-success' : 'bg-muted-foreground/10 text-muted-foreground'
                }`}>
                  {isConfigured ? 'Configured' : 'Not Configured'}
                </span>
                <span className="text-xs text-muted-foreground">Stored Locally</span>
              </div>
            </div>
            <div className="grid grid-cols-2 gap-4 text-sm">
              <div>
                <span className="text-muted-foreground">Provider:</span>
                <span className="ml-2 font-mono" data-testid="text-current-provider">
                  {LLM_PROVIDERS.find(p => p.value === provider)?.label || 'Not set'}
                </span>
              </div>
              <div>
                <span className="text-muted-foreground">Model:</span>
                <span className="ml-2 font-mono" data-testid="text-current-model">
                  {selectedModel || 'Not set'}
                </span>
              </div>
              {requiresEndpoint() && (
                <div className="col-span-2">
                  <span className="text-muted-foreground">Endpoint:</span>
                  <span className="ml-2 font-mono break-all" data-testid="text-current-endpoint">
                    {endpoint || 'Not set'}
                  </span>
                </div>
              )}
              {requiresApiKey() && (
                <div className="col-span-2">
                  <span className="text-muted-foreground">API Key:</span>
                  <span className="ml-2 font-mono" data-testid="text-current-api-key">
                    {apiKey ? '•'.repeat(20) : 'Not set'}
                  </span>
                </div>
              )}
            </div>
          </div>
        )}

        {/* Performance Stats */}
        {isConnected && (
          <>
            <div className="space-y-2">
              <Label className="text-sm font-medium text-foreground">Response Time</Label>
              <div className="text-sm text-muted-foreground">
                Last query: <span data-testid="text-llm-response-time">{responseTime}s</span>
              </div>
            </div>

            <div className="space-y-2">
              <Label className="text-sm font-medium text-foreground">Success Rate</Label>
              <div className="flex items-center space-x-2">
                <Progress value={successRate} className="flex-1" />
                <span className="text-sm text-success" data-testid="text-llm-success-rate">{successRate}%</span>
              </div>
            </div>
          </>
        )}

        {/* Action Buttons */}
        <div className="flex gap-3 pt-4">
          <Button 
            onClick={handleUpdateConfiguration}
            disabled={saveConfigurationMutation.isPending}
            className="flex-1 bg-primary text-primary-foreground hover:bg-primary/90"
            data-testid="button-update-llm-config"
          >
            {saveConfigurationMutation.isPending ? (
              <RefreshCw className="w-4 h-4 mr-2 animate-spin" />
            ) : (
              <Save className="w-4 h-4 mr-2" />
            )}
            Update Configuration
          </Button>

          <Button 
            onClick={() => testConnectionMutation.mutate()}
